 */
const JSONTryParse = (jsonString) => {
    try {
        return JSON.parse(jsonString);
    }
    catch (error) {
        return undefined;
    }
};

const useAvatarCreatorUrl = (subdomain, config) => {
    return useMemo(() => {
        let url = `https://${subdomain || `demo`}.readyplayer.me`;
        if (config?.language)
            url += `/${config.language}`;
        url += `/avatar?frameApi`;
        if (config?.clearCache)
            url += '&clearCache';
        if (config?.quickStart)
            url += '&quickStart';
        if (config?.bodyType)
            url += `&bodyType=${config?.bodyType}`;
        return url;
    }, [subdomain, config]);
};

const MESSAGE_EVENT = 'message';
const RPM_TARGET = 'readyplayerme';
const IFRAME_READY_EVENT = 'v1.frame.ready';
